    octant_vers_id = {}
   
    sequence_cryp = []
   
    unique_id = 1

    # divise le cryptogramme en octant   
    for i in range(0, len(C), 8):
    
        octant.append(C[i: i + 8])
    
    # donne la séquence du cryptogramme
    for binaire in octant:
    
        if binaire not in octant_vers_id:
    
            octant_vers_id[binaire] = unique_id
    
            unique_id += 1
    
        sequence_cryp.append(octant_vers_id[binaire])
    
    # analyse tout les extrait en symbole du texte ayant la même longueur que le cryptogramme en octant et ensuite compare la séquence de l'extrait à la séquence du cryptogramme
    for p in range(len(texte)) :
   
       for l in range(1, len(texte) - p):
   
          extrait = texte[p : p + l]
   
          extrait_vers_symb = []
   
          iterateur = 0
   
          while iterateur < len(extrait):
   
            if iterateur + 1 < len(extrait):
   
               pair = extrait[iterateur] + extrait[iterateur + 1]
   
               if pair in symb:
   
                extrait_vers_symb.append(pair)
   
                iterateur += 2 
           
                continue
            
            if extrait[iterateur] in symb:
           
                extrait_vers_symb.append(extrait[iterateur])
           
            else:
           
                extrait_vers_symb.append(extrait[iterateur])
           
            iterateur += 1

          if len(extrait_vers_symb) == len(octant):
             
             unique_num = 1
             
             extrait_vers_id = {}
             
             sequence_extrait = []
             
             for element in extrait_vers_symb:
             
                 if element not in extrait_vers_id:
             
                     extrait_vers_id[element] = unique_num
             
                     unique_num += 1
             
                 sequence_extrait.append(extrait_vers_id[element])
             
                 if sequence_extrait == sequence_cryp :
             
                     extrait = "".join(extrait)
             
                     return print(extrait)
          
          if len(extrait_vers_symb) > len(octant) + 2:
             
                break

